CREATE OR REPLACE FUNCTION pfcs_get_date_mask (ds_locale_p text) RETURNS varchar AS $body$
DECLARE

						
ds_formato_w	varchar(50);

BEGIN

if (ds_locale_p IS NOT NULL AND ds_locale_p::text <> '') then
	if (ds_locale_p = 'pt-BR') then
		ds_formato_w := 'DD/MM/YYYY HH:mm';
	else
		ds_formato_w := 'DD/MM/YYYY h:mm A';
	end if;
end if;

return ds_formato_w;

end;
