CREATE OR REPLACE FUNCTION pfcs_get_dnr_status_from_flag ( nr_seq_patient_p bigint ) RETURNS varchar AS $body$
DECLARE


	ie_dnr 			varchar(10) := 'DNR';
	ds_dnr_status 	varchar(10) := null;


BEGIN

	select 	cd_flag into STRICT ds_dnr_status
	from 	pfcs_patient_flag
	where 	nr_seq_patient = nr_seq_patient_p
			and coalesce(period_end::text, '') = ''
			and cd_flag = ie_dnr;

	return ds_dnr_status;

	exception
		-- If there are no records found
		when no_data_found then
			return ds_dnr_status;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
