SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pfcs_get_dnr_status ( nr_encounter text, cd_estabelecimento_p text ) RETURNS varchar AS $body$
DECLARE


ie_resposta 	varchar(10);
ds_dnr_status 	varchar(10) := null;


BEGIN

select ie_resposta
  into STRICT ie_resposta
  from (
		SELECT ie_resposta
        from diretriz_atendimento
        where nr_atendimento = nr_encounter
           and ie_diretriz = 'R'
           and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
           and coalesce(dt_inativacao::text, '') = '' 
           and coalesce(dt_final::text, '') = ''
           order by dt_liberacao) alias3 LIMIT 1;
		
 if (ie_resposta = 'N') then
     ds_dnr_status :=  'DNR';
 end if;

return ds_dnr_status;

exception
when no_data_found then
  return ds_dnr_status;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
