CREATE OR REPLACE FUNCTION pfcs_get_edi_score ( nr_sequencia_p bigint ) RETURNS bigint AS $body$
DECLARE

nr_retorno_w  bigint;

BEGIN
    select round(max(to_number(substr(replace(vl_referencia,',','.'), 0, 7), '999.999999999999999'))) into STRICT nr_retorno_w
    	FROM (
    		SELECT obs.vl_referencia
    		from pfcs_encounter enc,
    		pfcs_observation obs where
    		enc.nr_sequencia = obs.nr_seq_encounter
    		and obs.nr_seq_encounter =  nr_sequencia_p
    		and cd_observation_type = 'PFCS#edi-v2'
    		order by obs.dt_atualizacao DESC
    	 ) alias5 LIMIT 1;
return nr_retorno_w;
end;
