CREATE OR REPLACE FUNCTION pfcs_get_esi_rule ( nr_esi_p integer ) RETURNS varchar AS $body$
DECLARE

ie_resposta_w 	varchar(1);
ie_level_1_w varchar(1);
ie_level_2_w varchar(1);
ie_level_3_w varchar(1);
ie_level_4_w varchar(1);
ie_level_5_w varchar(1);

BEGIN

    select
		coalesce(max(ie_level_1),'S'),
		coalesce(max(ie_level_2),'S'),
		coalesce(max(ie_level_3),'N'),
		coalesce(max(ie_level_4),'N'),
		coalesce(max(ie_level_5),'N')
    into STRICT 
        ie_level_1_w,
        ie_level_2_w,
        ie_level_3_w,
        ie_level_4_w,
        ie_level_5_w
    from pfcs_rule_esi LIMIT 1;

	if (nr_esi_p = 1) then
		ie_resposta_w := ie_level_1_w;
	elsif (nr_esi_p = 2) then
		ie_resposta_w := ie_level_2_w;
	elsif (nr_esi_p = 3) then
		ie_resposta_w := ie_level_3_w;
	elsif (nr_esi_p = 4) then
		ie_resposta_w := ie_level_4_w;
	elsif (nr_esi_p = 5) then
		ie_resposta_w := ie_level_5_w;
	end if;

    return coalesce(ie_resposta_w,'N');

end;
$body$
