SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pfcs_get_frequent_flyer ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_retorno_w bigint;
ie_freq_flayer_status 		varchar(1) := null;


BEGIN

nr_retorno_w := 0;

select count(*) into STRICT nr_retorno_w  from (
    SELECT obs.*
    from pfcs_encounter enc,
    pfcs_observation obs where
    enc.nr_sequencia = obs.nr_seq_encounter
    and obs.nr_seq_encounter =  nr_sequencia_p
    and obs.nr_freq_flyer = 1
    and cd_observation_type = 'PFCS#FreqFlyer'
    order by obs.dt_atualizacao desc
) alias1 LIMIT 1;


if (nr_retorno_w > 0) then
    ie_freq_flayer_status := 'S';
end if;

return ie_freq_flayer_status;

	exception
		-- If there are no records found
		when no_data_found then
		  return ie_freq_flayer_status;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
