CREATE OR REPLACE FUNCTION pfcs_get_hours_telemetry_title ( cd_expressao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w		varchar(4000)	:= null;
cd_language_w           bigint          := null;
expression_w            varchar(4000)	:= null;
full_expression_w       varchar(4000)	:= null;
hours_abbrev_w          varchar(4000)	:= null;


BEGIN

	select philips_param_pck.get_nr_seq_idioma
	  into STRICT cd_language_w 
	;

	select obter_desc_expressao_idioma(cd_expressao_p, null, cd_language_w)	
	  into STRICT expression_w
	;

	select replace(expression_w, regexp_replace(expression_w, '[^0-9]', ''), coalesce(max(qt_time_on_tele__rule),12))
	  into STRICT ds_return_w
	  from pfcs_telemetry_config;

	return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
