CREATE OR REPLACE FUNCTION pfcs_get_indicator_name (nr_seq_indicator_p bigint) RETURNS varchar AS $body$
DECLARE


ds_indicator_w		varchar(255);


BEGIN

	select CASE nr_seq_indicator_p
		WHEN 326 THEN PFCS_PCK_UTILS.GET_PENDING_SURGERIES_TITLE(a.cd_exp_indicator)
		WHEN 51  THEN substr(pfcs_get_hours_telemetry_title(a.cd_exp_indicator), 1, 255)
		WHEN 380 THEN obter_desc_expressao(653330) || ' ' || obter_desc_expressao(999503)
		WHEN 381 THEN obter_desc_expressao(653330) || ' ' || obter_desc_expressao(999613)
        ELSE substr(obter_desc_expressao(a.cd_exp_indicator), 1, 255) END
    into STRICT 	ds_indicator_w
    from 	pfcs_indicator a
    where 	a.nr_sequencia = nr_seq_indicator_p;

return ds_indicator_w;
exception
          when no_data_found then return null;
          when too_many_rows then raise;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
