SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pfcs_get_lace_plus ( nr_sequencia_p bigint, nr_seq_organization_p bigint ) RETURNS varchar AS $body$
DECLARE

ds_return       varchar(255) := '';
ds_lace_plus    varchar(15) := 'PFCS#LacePlus';


BEGIN

select vl_referencia
into STRICT ds_return
from (SELECT coalesce(obs.vl_referencia,0) vl_referencia
            from pfcs_encounter enc,
            pfcs_observation obs
            where enc.nr_sequencia = obs.nr_seq_encounter
                and obs.nr_seq_encounter =  nr_sequencia_p
                and obs.cd_observation_type = ds_lace_plus
                and enc.nr_seq_organization = nr_seq_organization_p
            order by obs.dt_atualizacao desc) alias1 LIMIT 1;

return(concat(round((to_number(substr(replace(ds_return,',','.'), 0, 4), '9999D99', 'NLS_NUMERIC_CHARACTERS = ".,"'))::numeric,0), '%'));

exception
    when data_exception then
    return 0;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
