CREATE OR REPLACE FUNCTION pfcs_get_order_ed ( cd_estabelecimento_p bigint, nr_seq_indicator_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_rownum_w			bigint := 0;


BEGIN
SELECT
    (nr_rownum)::numeric  INTO STRICT nr_rownum_w
FROM (
        SELECT
            row_number() OVER () AS nr_rownum,
            nr_seq_indicator,
            nr_seq_order
        FROM (
                SELECT DISTINCT
                    nr_seq_indicator,
                    ie_disabled,
                    (pfcs_get_indicator_rule(nr_seq_indicator, vl_target, cd_estabelecimento, 'ORDER'))::numeric  nr_seq_order
                FROM
                    pfcs_ed_dpts_v
                WHERE
                    cd_estabelecimento = cd_estabelecimento_p
                    AND pfcs_get_indicator_rule(nr_seq_indicator, vl_target, cd_estabelecimento, 'VISIBLE') <> 'N'
                ORDER BY
                    nr_seq_order ASC
            ) alias4
    ) alias5
WHERE
    nr_seq_indicator = nr_seq_indicator_p;

     RETURN nr_rownum_w;
    EXCEPTION
		-- If there are no records found
		WHEN no_data_found THEN
		  RETURN 0;



END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
