SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pfcs_get_patient_diagnosis ( nr_seq_encounter_p bigint ) RETURNS varchar AS $body$
DECLARE


	ds_reason_w 		varchar(300);
	ds_reason_aux_w 	varchar(300);
	ds_diagnosis_w		varchar(300):= '';
	

BEGIN

	select ds_reason, ds_reason_aux
	into STRICT ds_reason_w, ds_reason_aux_w
	from pfcs_encounter
	where nr_sequencia = nr_seq_encounter_p;

	if ((ds_reason_w IS NOT NULL AND ds_reason_w::text <> '') and (ds_reason_aux_w IS NOT NULL AND ds_reason_aux_w::text <> '') and ds_reason_w <> ds_reason_aux_w) THEN
		ds_diagnosis_w := ds_reason_w || ', ' || ds_reason_aux_w;
	else ds_diagnosis_w := ds_reason_w;		
	end if;
	
	return ds_diagnosis_w;

	exception
		-- If there are no records found
		when no_data_found then
		  return ds_diagnosis_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
