CREATE OR REPLACE FUNCTION pfcs_get_patient_gender ( ds_gender_p text ) RETURNS varchar AS $body$
DECLARE


  ie_resposta_w     varchar(1);

BEGIN

	if (upper(ds_gender_p) = 'MALE') then
        ie_resposta_w :=  'M';
    elsif (upper(ds_gender_p) = 'FEMALE') then
        ie_resposta_w :=  'F';
    ELSE
		ie_resposta_w := 'U';
    end if;
	
	return ie_resposta_w;

	exception
		-- If there are no records found
		when no_data_found then
		  return ds_gender_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
