CREATE OR REPLACE FUNCTION pfcs_get_pa_period () RETURNS timestamp AS $body$
DECLARE


dt_base_w		timestamp;
ie_period_pa_w		pfcs_general_rule.ie_period_pa%type;


BEGIN

	select	ie_period_pa
	into STRICT	ie_period_pa_w
	from	pfcs_general_rule LIMIT 1;

	if (ie_period_pa_w = 'D') then
		select trunc(clock_timestamp()) into STRICT dt_base_w;
	elsif (ie_period_pa_w = 'U') then
		select clock_timestamp() - interval '1 days' into STRICT dt_base_w;
	end if;

	return dt_base_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
