CREATE OR REPLACE FUNCTION pfcs_get_percentage_value ( vl_dividend_p bigint, vl_divisor_p bigint) RETURNS bigint AS $body$
DECLARE


vl_result_w		bigint;


BEGIN

if (coalesce(vl_divisor_p,0) = 0) then
	vl_result_w	:= 0;
else
	vl_result_w	:= (coalesce(vl_dividend_p,0) / vl_divisor_p) * 100;
end if;

return	vl_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
