CREATE OR REPLACE FUNCTION pfcs_get_practitioner_seq ( nr_seq_encounter_p bigint, cd_role_p text default '405279007') RETURNS bigint AS $body$
DECLARE


	nr_seq_practitioner 	bigint := null;

BEGIN

	select nr_sequencia into STRICT nr_seq_practitioner
	from (
		SELECT prac.nr_sequencia
		from 	pfcs_encounter_participant ep,
				pfcs_practitioner prac,
				pfcs_practitioner_role ppr
		where 
				ep.nr_seq_encounter = nr_seq_encounter_p
				and coalesce(ep.end_date::text, '') = ''
				and ppr.nr_seq_practitioner = ep.nr_seq_practitioner
				and ppr.cd_role = cd_role_p
				and prac.nr_sequencia = ppr.nr_seq_practitioner
				and prac.si_status = 1
		order by ep.start_date desc
	) alias1 LIMIT 1;

	return nr_seq_practitioner;

	exception
		-- If there are no records found
		when no_data_found then
		  return nr_seq_practitioner;

end;
