CREATE OR REPLACE FUNCTION pfcs_get_status_bed (nr_sequencia_p bigint, ie_type bigint default 1) RETURNS varchar AS $body$
DECLARE


ds_reason varchar(20);
ie_reason varchar(2);


BEGIN

    select ds_operational_status,
           cd_operational_status
     into STRICT   ds_reason,
            ie_reason
    from pfcs_location
    where nr_sequencia = nr_sequencia_p;

    if (ie_type = 1) then
        return ds_reason;
    else
        return ie_reason;
    end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
