SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pfcs_get_structure_level (cd_establishment_p bigint, ie_level_p text, ie_info_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_operational_w		pfcs_operational_level.nr_sequencia%type;
ds_level_operational_w		pfcs_operational_level.ds_level%type;

nr_seq_tactical_w		pfcs_tactical_level.nr_sequencia%type;
ds_level_tactical_w		pfcs_tactical_level.ds_level%type;

/*
ie_level:
O = operational
T = tactical

ie_info:
C = nr_sequencia
D = ds_level
*/
BEGIN

select
	a.nr_sequencia,
	a.ds_level,
	b.nr_sequencia,
	b.ds_level
into STRICT
	nr_seq_operational_w,
	ds_level_operational_w,
	nr_seq_tactical_w,
	ds_level_tactical_w
from
	pfcs_operational_level a,
	pfcs_tactical_level b
where	a.nr_seq_tactical_level = b.nr_sequencia
and	a.cd_establishment = cd_establishment_p;

if (ie_level_p = 'O') then
	if (ie_info_p = 'C') then
		return nr_seq_operational_w;--to_char??
	elsif (ie_info_p = 'D') then
		return ds_level_operational_w;
	end if;
elsif (ie_level_p = 'T') then
	if (ie_info_p = 'C') then
		return nr_seq_tactical_w;--to_char??
	elsif (ie_info_p = 'D') then
		return ds_level_tactical_w;
	end if;
end if;

return '';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
