CREATE OR REPLACE FUNCTION pfcs_is_tasy_call_enabled ( nr_seq_wcpanel_p bigint, cd_function_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w  PFCS_INDICATOR.DS_FUNCTIONS_CALL%type;


BEGIN
if (nr_seq_wcpanel_p IS NOT NULL AND nr_seq_wcpanel_p::text <> '' AND cd_function_p IS NOT NULL AND cd_function_p::text <> '') then
	SELECT coalesce(max(PFCS_PCK_CONSTANTS.DS_ENABLED), PFCS_PCK_CONSTANTS.DS_DISABLED)
	INTO STRICT ds_return_w
	FROM PFCS_INDICATOR
	WHERE NR_SEQ_LIST = nr_seq_wcpanel_p
	AND  obter_se_contido_entre_virgula(DS_FUNCTIONS_CALL,cd_function_p) = PFCS_PCK_CONSTANTS.IE_YES_BR;
end if;
return ds_return_w;
end;
