SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pfcs_lab_ie_teste_rapido_covid ( cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE

   ds_resultado_pac_w bigint;
   ds_resultado_lab_w bigint;

BEGIN
   SELECT
      COUNT(1)
   INTO STRICT
      ds_resultado_lab_w
   FROM  	 
      pessoa_fisica a,
		prescr_medica b,
		prescr_procedimento c,
		exame_lab_resultado d,
		exame_lab_result_item e,
		equipamento_lab f,
		lab_exame_equip g
   WHERE
      a.cd_pessoa_fisica = b.cd_pessoa_fisica
      AND b.nr_prescricao = c.nr_prescricao
      AND c.nr_sequencia = e.nr_seq_prescr
      AND b.nr_prescricao = d.nr_prescricao
      AND d.nr_seq_resultado = e.nr_seq_resultado
      AND f.cd_equipamento = g.cd_equipamento
      AND UPPER(f.ds_sigla) = 'COVID19'
      AND g.nr_seq_exame = c.nr_seq_exame
      AND (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
      AND c.ie_status_atend >= 35
      AND pfcs_lab_obter_se_exame_covid(c.nr_seq_exame) = 'S'
      AND 
         (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND b.cd_pessoa_fisica = cd_pessoa_fisica_p);

   SELECT
      COUNT(1)
   INTO STRICT
      ds_resultado_pac_w
   FROM
      paciente_exame a,
      atendimento_paciente b
   WHERE
      a.ie_exame_pac = 'COVID-19' 
      AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
      AND coalesce(a.dt_inativacao::text, '') = ''
      AND (a.ds_resultado IS NOT NULL AND a.ds_resultado::text <> '')
      AND a.cd_pessoa_fisica = b.cd_pessoa_fisica
      AND 
         (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND b.cd_pessoa_fisica = cd_pessoa_fisica_p);
   IF (ds_resultado_lab_w = 0 AND ds_resultado_pac_w != 0) then
      return 'S';
   ELSE
      return 'N';
   END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
