CREATE OR REPLACE FUNCTION pfcs_obter_desc_cid ( CD_DOENCA_CID_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(300);


BEGIN
if (CD_DOENCA_CID_P IS NOT NULL AND CD_DOENCA_CID_P::text <> '') then
	begin
        select  MAX(DS_DOENCA_CID)
