SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pfcs_obter_lista_dados_classif (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000) := '';
ds_classif_w		varchar(80);

C01 CURSOR FOR
	SELECT	substr(obter_classif_pessoa(b.nr_seq_classif),1,80) ds_classif
	from	pessoa_classif b, classif_pessoa a
	where	b.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(b.dt_inativacao::text, '') = ''
	and	b.dt_inicio_vigencia <= clock_timestamp()
	and	((coalesce(b.dt_final_vigencia::text, '') = '') or (b.dt_final_vigencia >= clock_timestamp()))
	and	b.nr_seq_classif = a.nr_sequencia
	and	coalesce(a.ie_exibe_pfcs, 'N') = 'S'
	and	a.ie_situacao = 'A'
	order by 1;
	
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
   for c01_w in c01 loop
      begin
         ds_retorno_w := ds_retorno_w || ', ' || c01_w.ds_classif;
      end;
   end loop;
end if;

ds_retorno_w := substr(ds_retorno_w, 2, Length(ds_retorno_w) - 1);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pfcs_obter_lista_dados_classif (cd_pessoa_fisica_p text) FROM PUBLIC;

