CREATE OR REPLACE FUNCTION phi_get_lib_classif_status ( nr_seq_classificacao_p classificacao_projeto.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


/*
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Objective: Return if the User has the permission to change the project
		status.
	------------------------------------------------------------------------
	Direct call: 
	[   ] Dictionary objects
	[ X ] Tasy (Delphi/Java/HTML5)
	[   ] Portal
	[   ] Reports
	[   ] Others:
	 -----------------------------------------------------------------------
	Attention points: N/A
	------------------------------------------------------------------------
	References:
		Tables: N/A
		Objects: N/A
	------------------------------------------------------------------------
	Called in:
		Java: N/A
		HTML5: N/A
		Object: N/A
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
*/
cd_perfil_w		perfil.cd_perfil%type;
nm_usuario_w		usuario.nm_usuario%type;

ds_retorno_w		varchar(4000);


BEGIN

	select	
		wheb_usuario_pck.get_cd_perfil,
		wheb_usuario_pck.get_nm_usuario
	into STRICT	cd_perfil_w,
		nm_usuario_w
	;
	
	begin
		select	'S'
		into STRICT	ds_retorno_w
		from	classificacao_projeto_lib cpl
		where	cpl.nr_seq_classificacao = nr_seq_classificacao_p
		and 	cpl.nm_usuario_lib = nm_usuario_w;
	exception
	when others then
		begin
			select	'S'
			into STRICT	ds_retorno_w
			from	classificacao_projeto_lib cpl
			where	cpl.nr_seq_classificacao = nr_seq_classificacao_p
			and	cpl.cd_perfil = cd_perfil_w;
		exception
		when others then
			begin
				select	'S'
				into STRICT	ds_retorno_w
				from	classificacao_projeto_lib cpl
				where	coalesce(cpl.nm_usuario_lib::text, '') = ''
				and	coalesce(cpl.cd_perfil::text, '') = ''
				and	cpl.nr_seq_classificacao = nr_seq_classificacao_p;
			exception
			when others then
				ds_retorno_w := 'N';
			end;
		end;
	end;	

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
