CREATE OR REPLACE FUNCTION phi_get_obs_os_proj ( nr_seq_proj_ordem_serv_p proj_ordem_servico.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);


BEGIN

	select	max(substr(x.observacao_bruta, position(';' in x.observacao_bruta) + 1, length(x.observacao_bruta)))
	into STRICT	ds_retorno_w
	from (
			SELECT	max(duv_obter_texto_html(substr(pos.ds_observacao, 1, 4000))) observacao_bruta
			from	proj_ordem_servico pos
			where	pos.nr_sequencia = nr_seq_proj_ordem_serv_p
		) x;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
