SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION phi_obter_exige_release_notes (nr_seq_ordem_serv_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w varchar(1);
ie_medical_device_w		varchar(1);
ie_classificacao_w 		varchar(10);
nr_seq_classif_cad_w		regra_release_news.nr_seq_classif_cad%type;
ie_obriga_release_news_w 	regra_release_news.ie_obriga_release_news%type;
ie_terceiro_w			man_localizacao.ie_terceiro%type;
nr_sequencia_regra_w		regra_release_news.nr_sequencia%type;
cd_funcao_os_w			man_ordem_servico.cd_funcao%type;
nr_seq_grupo_des_os_w		man_ordem_servico.nr_seq_grupo_des%type;
nr_seq_gerencia_w		grupo_desenvolvimento.nr_seq_gerencia%type;
nr_seq_severidade_wheb_w	man_ordem_servico.nr_seq_severidade_wheb%type;
ie_projeto_os_w			varchar(1);


c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ie_obriga_release_news
from	regra_release_news a
where	((cd_funcao_os_w  (	select	b.cd_funcao
				from	regra_fun_release_news b
				where	b.nr_seq_regra = a.nr_sequencia)
			and	exists (	select 1
						from	regra_fun_release_news b
						where	b.nr_seq_regra = a.nr_sequencia))
			or	not exists (	select	1
						from regra_fun_release_news b
						where	b.nr_seq_regra = a.nr_sequencia))
and	((nr_seq_grupo_des_os_w in (	select	b.nr_seq_grupo_des
					from	regra_grupo_release_news b
					where	b.nr_seq_regra = a.nr_sequencia)
		and	exists (	select	1
					from	regra_grupo_release_news b
					where	b.nr_seq_regra = a.nr_sequencia))
		or	not exists (	select	1
					from	regra_grupo_release_news b
					where	b.nr_seq_regra = a.nr_sequencia))
and	((nr_seq_gerencia_w in (	select	b.nr_seq_gerencia
					from	regra_ger_release_news b
					where	b.nr_seq_regra = a.nr_sequencia)
			and	exists (	select	1
						from	regra_ger_release_news b
						where	b.nr_seq_regra = a.nr_sequencia))
			or	not exists (	select	1
						from	regra_ger_release_news b
						where	b.nr_seq_regra = a.nr_sequencia))
and	coalesce(a.ie_projeto, 'N') = ie_projeto_os_w
and	coalesce(a.ie_classificacao, ie_classificacao_w) = ie_classificacao_w
and	coalesce(a.nr_seq_classif_cad, coalesce(nr_seq_classif_cad_w, -9999)) = coalesce(nr_seq_classif_cad_w, -9999)
and	((nr_seq_severidade_wheb_w in (	select	b.nr_seq_severidade
						from	regra_sever_release_news b
						where	b.nr_seq_regra = a.nr_sequencia)
		and	exists (	select	1
					from	regra_sever_release_news b
					where	b.nr_seq_regra = a.nr_sequencia))
		or	not exists (	select	1
					from	regra_sever_release_news b
					where	b.nr_seq_regra = a.nr_sequencia))
and	((coalesce(a.ie_medical_device, ie_medical_device_w) = ie_medical_device_w) or (a.ie_medical_device = 'A'))
and	((coalesce(a.ie_terceiro, ie_terceiro_w) = ie_terceiro_w) or (a.ie_terceiro = 'A'))
order by 	coalesce((	select	distinct b.cd_funcao
			from	regra_fun_release_news b
			where	b.nr_seq_regra = a.nr_sequencia
			and	b.cd_funcao = cd_funcao_os_w), 999999999) desc,
			coalesce((	select	b.nr_seq_grupo_des
				from	regra_grupo_release_news b
				where	b.nr_seq_regra = a.nr_sequencia
				and	b.nr_seq_grupo_des = nr_seq_grupo_des_os_w), 999999999) desc,
			coalesce((	select	b.nr_seq_gerencia
				from	regra_ger_release_news b
				where	b.nr_seq_regra = a.nr_sequencia
				and	b.nr_seq_gerencia = nr_seq_gerencia_w), 999999999) desc,
			coalesce(a.ie_projeto, 'ZZZZZZZZZZZ') desc,
			coalesce(a.ie_classificacao, 'ZZZZZZZZZZZ') desc,
			coalesce(a.nr_seq_classif_cad, 999999999) desc,
			coalesce(a.ie_medical_device, 'ZZZZZZZZZZZ') desc,
			coalesce(a.ie_terceiro, 'ZZZZZZZZZZZ') desc;


BEGIN

select 	coalesce(max(b.ie_clinico), 'N') is_medical_device
into STRICT	ie_medical_device_w
from 	man_ordem_servico a,
	reg_customer_requirement b
where 	b.nr_sequencia 	= a.nr_customer_requirement
and	a.nr_sequencia	= nr_seq_ordem_serv_p
and 	b.ie_situacao 	= 'A';

select	max(x.ie_terceiro)
into STRICT	ie_terceiro_w
from	man_ordem_servico a,
	man_localizacao x
where	x.nr_sequencia = a.nr_seq_localizacao
and	a.nr_sequencia = nr_seq_ordem_serv_p;

select	a.ie_classificacao,
	a.nr_seq_classif_cad,
	CASE WHEN coalesce(a.nr_seq_proj_cron_etapa::text, '') = '' THEN  'N'  ELSE 'S' END  ie_projeto,
	a.cd_funcao,
	a.nr_seq_grupo_des,
	a.nr_seq_severidade_wheb
into STRICT	ie_classificacao_w,
	nr_seq_classif_cad_w,
	ie_projeto_os_w,
	cd_funcao_os_w,
	nr_seq_grupo_des_os_w,
	nr_seq_severidade_wheb_w
from	man_ordem_servico a
where	a.nr_seq_estagio <> 2
and	coalesce(a.nr_seq_ordem_serv_pai::text, '') = ''
and not exists (	SELECT	1
		from	release_news
		where	nr_seq_ordem_serv = a.nr_sequencia
		and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
and exists (	select	1
		from	man_ordem_serv_tecnico c
		where	nr_seq_ordem_serv = a.nr_sequencia
		and	nr_seq_tipo = 163)
and	a.nr_sequencia = nr_seq_ordem_serv_p;

if (nr_seq_grupo_des_os_w IS NOT NULL AND nr_seq_grupo_des_os_w::text <> '') then
	select	max(nr_seq_gerencia)
	into STRICT	nr_seq_gerencia_w
	from	grupo_desenvolvimento
	where	nr_sequencia	= nr_seq_grupo_des_os_w;
end if;

	open c01;
	loop
	fetch c01 into
		nr_sequencia_regra_w,
		ie_obriga_release_news_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;

if (nr_sequencia_regra_w IS NOT NULL AND nr_sequencia_regra_w::text <> '') then
	ie_retorno_w := ie_obriga_release_news_w;
else
	ie_retorno_w := 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
