SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION planserv_obter_agrupamento_mat (nr_seq_item_p bigint, ie_retorno_p text, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
cd_grupo_w	conversao_material_convenio.cd_grupo%type;
cd_codigo_w	bigint;
ds_agrup_w	varchar(255);
/*
I (INDICADOR DOS MAT/MED CONFORME CONVERSAO)
	INDICADOR MAT/MED
	MEDICAMENTO - 2
	CONTRASTES - 5
	MATERIAIS - 1
	ORTESES E PROTESES - 3
	SINTESE E ESPECIAIS - 4


A (AGRUPAMENTO CÓDIGOS FIXOS)

	MEDICAMENTO - 63130025
	cONTRASTES - 63130033
	MATERIAIS - 63130017
	ORTESES E PROTESES - 63130041
	SINTESE E ESPECIAIS - 63130050

D -  DESCRIÇÃO DOS AGRUPAMENTOS

	MEDICAMENTO - 63130025
	CONTRASTES - 63130033
	MATERIAIS - 63130017
	ORTESES E PROTESES - 63130041
	SINTESE E ESPECIAIS - 63130050

*/
BEGIN


SELECT	max(x.cd_grupo)
into STRICT	cd_grupo_w
FROM 	mat_atend_pac_convenio x
WHERE 	x.nr_seq_material = nr_seq_item_p
and	ie_tipo_item_p	  = 2;

if (coalesce(cd_grupo_w,'0') = '1') then

	cd_codigo_w := 63130017;
	ds_agrup_w  := 'MATERIAIS';

elsif (coalesce(cd_grupo_w,'0') = '2') then

	cd_codigo_w := 63130025;
	ds_agrup_w  := 'MEDICAMENTOS';
elsif (coalesce(cd_grupo_w,'0') = '3') then

	cd_codigo_w := 63130041;
	ds_agrup_w  := 'ORTESES E PROTESES';

elsif (coalesce(cd_grupo_w,'0') = '4') then

	cd_codigo_w := 63130050;
	ds_agrup_w  := 'SINTESE E ESPECIAIS';
elsif (coalesce(cd_grupo_w,'0') = '5') then

	cd_codigo_w := 63130033;
	ds_agrup_w  := 'CONTRASTES';
end if;


if (ie_retorno_p = 'I') then
	ds_retorno_w := cd_grupo_w;
elsif (ie_retorno_p = 'A') then
	ds_retorno_w := cd_codigo_w;
elsif (ie_retorno_p = 'D') then
	ds_retorno_w := ds_agrup_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
