SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION planserv_obter_dados_cat_conv ( nr_atendimento_p bigint, nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

				 
/*	opção 
B	- Biometria	 
S	- Senha 
*/
 
				 
ds_retorno_w			varchar(255) := '';
ie_tipo_guia_w			conta_paciente.ie_tipo_guia%type;
nr_biometria_conv_w		atend_categoria_convenio.nr_biometria_conv%type;
nr_seq_interno_w		atend_categoria_convenio.nr_seq_interno%type;
cd_senha_guia_w			atend_categoria_convenio.cd_senha%type;


BEGIN 
 
if (ie_opcao_p in ('B', 'S')) then 
 
	begin 
		select	coalesce(max(ie_tipo_guia), 'X') 
		into STRICT	ie_tipo_guia_w 
		from	conta_paciente	 
		where 	nr_interno_conta = nr_interno_conta_p;
	exception 
		when others then 
			ie_tipo_guia_w	:= 'X';
	end;
	 
	if (ie_tipo_guia_w <> 'X') then 
	 
		begin 
		select	max(nr_biometria_conv), 
				max(cd_senha) 
		into STRICT	nr_biometria_conv_w, 
				cd_senha_guia_w 
		from	atend_categoria_convenio 
		where	ie_tipo_guia	= ie_tipo_guia_w 
		and	nr_atendimento	= nr_atendimento_p;
		exception 
			when others then 
				nr_biometria_conv_w	:= null;
				cd_senha_guia_w		:= 0;
		end;
	 
	end if;
		 
	if (ie_tipo_guia_w = 'X') or ((coalesce(nr_biometria_conv_w,0) = 0) or (ie_opcao_p = 'B')) then 
	 
		select	max(obter_atecaco_atendimento(nr_atendimento_p)) 
		into STRICT	nr_seq_interno_w 
		;
		 
		begin 
		select	nr_biometria_conv 
		into STRICT	nr_biometria_conv_w 
		from	atend_categoria_convenio 
		where	nr_seq_interno	= nr_seq_interno_w 
		and	nr_atendimento	= nr_atendimento_p;
		exception 
			when others then 
				nr_biometria_conv_w	:= null;
		end;
	 
	end if;
	 
	if (ie_opcao_p = 'B') then 
		ds_retorno_w := nr_biometria_conv_w;
	elsif (ie_opcao_p = 'S') then	 
		ds_retorno_w := cd_senha_guia_w;
	end if;
 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
