CREATE OR REPLACE FUNCTION planserv_obter_parcela_conta (cd_pessoa_fisica_p text, nr_interno_conta_p bigint, nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_posicao_conta_w	bigint := 1;			
nr_seq_posicao_w		bigint := 0;
nr_interno_conta_w		bigint;
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
ie_tipo_atend_conta_w	conta_paciente.ie_tipo_atend_conta%type;

C01 CURSOR FOR 
	SELECT	row_number() OVER () AS rownum, 
		nr_interno_conta 
	FROM (SELECT	a.nr_interno_conta 
		FROM	conta_paciente a, 
			atendimento_paciente b 
		WHERE	a.nr_atendimento  = b.nr_atendimento 
		AND	b.nr_atendimento = nr_atendimento_w	 
		--and	b.ie_tipo_atendimento = 1 
		ORDER BY a.dt_periodo_inicial) alias0 
	WHERE	1 = 1;
			
			 

BEGIN 
 
select	nr_atendimento, 
	ie_tipo_atend_conta 
into STRICT	nr_atendimento_w, 
	ie_tipo_atend_conta_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
if (ie_tipo_atend_conta_w = 1) then 
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_posicao_w, 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		if (nr_interno_conta_w = nr_interno_conta_p) then 
			nr_seq_posicao_conta_w := nr_seq_posicao_w;
		end if;
	end loop;
	close C01;
	 
end if;
 
return	nr_seq_posicao_conta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
