CREATE OR REPLACE FUNCTION plansev_obter_funcao_medico (cd_funcao_executor_p bigint) RETURNS bigint AS $body$
DECLARE

cd_funcao_w		smallint;
ie_anestesista_w	funcao_medico.ie_anestesista%type;
ie_auxiliar_w		funcao_medico.ie_auxiliar%type;
ie_cirurgiao_w		funcao_medico.ie_cirurgiao%type;
ie_medico_w		funcao_medico.ie_medico%type;



BEGIN
if (coalesce(cd_funcao_executor_p,0) > 0) then

	begin
	SELECT 	IE_ANESTESISTA,
		IE_AUXILIAR,
		IE_CIRURGIAO,
		IE_MEDICO
	INTO STRICT 	ie_anestesista_w,
		ie_auxiliar_w,
		ie_cirurgiao_w,
		ie_medico_w
	from 	funcao_medico
	WHERE 	cd_funcao = cd_funcao_executor_p;
	exception
	when others then
		ie_cirurgiao_w := 'N';
		ie_auxiliar_w  := 'N';
		ie_anestesista_w := 'N';
	end;


	if (ie_cirurgiao_w = 'S') THEN
		cd_funcao_w := 0;
	elsif (ie_auxiliar_w = 'S' and ie_medico_w = 'S') THEN
		cd_funcao_w := 1;
	elsif (ie_anestesista_w = 'S') THEN
		cd_funcao_w := 5;
	end if;



end if;

return	cd_funcao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
