CREATE OR REPLACE FUNCTION ple_atualiza_lib_edicao ( nr_seq_edicao_p bigint, cd_perfil_p bigint, nm_usuario_lib_p text) RETURNS varchar AS $body$
DECLARE


ie_atualizacao_w	varchar(1);


BEGIN

select	coalesce(max(ie_atualizacao),'S')
into STRICT	ie_atualizacao_w
from	ple_edicao_liberacao
where	nr_seq_edicao = nr_seq_edicao_p
and	((cd_perfil = cd_perfil_p) or (nm_usuario_lib = nm_usuario_lib_p));

RETURN ie_atualizacao_w;

END;
