SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ple_obter_acesso_edicao ( nr_seq_edicao_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w			varchar(1);
nm_usuario_lib_w		varchar(15);
nr_seq_edicao_w			bigint;
cd_perfil_ativo_w		bigint;
cd_perfil_w			bigint;
qt_reg_w			bigint;
cd_setor_atendimento_w		bigint;
cd_setor_ativo_w		bigint;

c01 CURSOR FOR
SELECT	cd_perfil,
	nm_usuario_lib,
	coalesce(ie_atualizacao,'N') ie_atualizacao,
	cd_setor_atendimento
from	ple_edicao_liberacao a
where	a.nr_seq_edicao					= nr_seq_edicao_w
and	coalesce(a.cd_perfil, cd_perfil_ativo_w)		= cd_perfil_ativo_w
and	coalesce(a.cd_setor_atendimento, cd_setor_ativo_w)	= cd_setor_ativo_w
and	coalesce(a.nm_usuario_lib, nm_usuario_p)		= nm_usuario_p
and	coalesce(a.nr_seq_classif::text, '') = ''
order by	coalesce(a.nm_usuario_lib, 'A'),
		coalesce(a.cd_perfil, 0),
		coalesce(a.cd_setor_atendimento,0);


BEGIN

nr_seq_edicao_w		:= coalesce(nr_seq_edicao_p, 0);

begin
	cd_perfil_ativo_w	:= coalesce(wheb_usuario_pck.get_cd_perfil,0);
	cd_setor_ativo_w	:= coalesce(wheb_usuario_pck.get_cd_setor_atendimento,0);
exception when others then
	cd_setor_ativo_w	:= 0;
	cd_perfil_ativo_w	:= 0;
end;

ds_resultado_w		:= 'N';

select	count(*)
into STRICT	qt_reg_w
from	ple_edicao_liberacao
where	nr_seq_edicao	= nr_seq_edicao_w
and	coalesce(nr_seq_classif::text, '') = '';

if (qt_reg_w = 0) then
	ds_resultado_w	:= 'S';
else
	ds_resultado_w	:= 'N';

	open c01;
	loop
	fetch c01 into
		cd_perfil_w,
		nm_usuario_lib_w,
		ds_resultado_w,
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_resultado_w	:= ds_resultado_w;

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then

			select	coalesce(max('S'),'N')
			into STRICT	ds_resultado_w
			from	usuario_perfil b
			where	b.cd_perfil	= cd_perfil_w
			and	b.nm_usuario	= nm_usuario_p
			and	ds_resultado_w	= 'S';

		end if;

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') then
			begin

			select	coalesce(max('S'),'N')
			into STRICT	ds_resultado_w
			from	usuario_setor
			where	nm_usuario		= nm_usuario_p
			and	cd_setor_atendimento	= cd_setor_atendimento_w
			and	ds_resultado_w		= 'S';
			end;
		end if;

		end;
	end loop;
	close c01;

end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
