SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ple_obter_acesso_objetivo ( nr_seq_edicao_p bigint, nr_seq_perspectiva_p bigint, nr_seq_tema_p bigint, nr_seq_objetivo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_reg_w			bigint;
cd_perfil_w			bigint;
ie_atualizacao_w		varchar(1)	:= 'S';
nm_usuario_lib_w		varchar(15);
nr_seq_perspectiva_w		bigint;
nr_seq_tema_w			bigint;
nr_seq_objetivo_w		bigint;
cd_perfil_ativo_w		bigint;
cd_setor_atendimento_w		bigint;
cd_setor_ativo_w		bigint;

c01 CURSOR FOR
SELECT	cd_perfil,
	nm_usuario_lib,
	coalesce(ie_atualizacao,'N') ie_atualizacao,
	cd_setor_atendimento
from	ple_edicao_liberacao a
where	a.nr_seq_edicao				= nr_seq_edicao_p
and	coalesce(a.nr_seq_classif, nr_seq_perspectiva_w)	= nr_seq_perspectiva_w
and	coalesce(a.nr_seq_tema, nr_seq_tema_w)		= nr_seq_tema_w
and	coalesce(a.nr_seq_objetivo, nr_seq_objetivo_w)	= nr_seq_objetivo_w
and	coalesce(a.cd_setor_atendimento, cd_setor_ativo_w)	= cd_setor_ativo_w
and	coalesce(a.cd_perfil, cd_perfil_ativo_w)		= cd_perfil_ativo_w
and	coalesce(a.nm_usuario_lib, nm_usuario_p)		= nm_usuario_p;


BEGIN

nr_seq_perspectiva_w	:= coalesce(nr_seq_perspectiva_p,0);
nr_seq_tema_w		:= coalesce(nr_seq_tema_p,0);
nr_seq_objetivo_w	:= coalesce(nr_seq_objetivo_p,0);

begin
select	wheb_usuario_pck.get_cd_perfil
into STRICT	cd_perfil_ativo_w
;

cd_setor_ativo_w	:= coalesce(wheb_usuario_pck.get_cd_setor_atendimento,0);

exception when others then
	cd_perfil_ativo_w	:= 0;
	cd_setor_ativo_w	:= 0;
end;

/*Buscar Tema e Perspectiva do Objetivo se tiver OBJ informado */

if (nr_seq_objetivo_w <> 0) then
	select	coalesce(a.nr_seq_classif,0),
		coalesce(a.nr_seq_tema,0)
	into STRICT	nr_seq_perspectiva_w,
		nr_seq_tema_w
	from	ple_objetivo a
	where	a.nr_sequencia = nr_seq_objetivo_p;
end if;

select	count(*)
into STRICT	qt_reg_w
from	ple_edicao_liberacao a
where	a.nr_seq_edicao	= nr_seq_edicao_p;

if (qt_reg_w > 0) then

	ie_atualizacao_w	:= 'N';

	open C01;
	loop
	fetch C01 into
		cd_perfil_w,
		nm_usuario_lib_w,
		ie_atualizacao_w,
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then

			select	coalesce(max('S'),'N')
			into STRICT	ie_atualizacao_w
			from	usuario_perfil b
			where	b.cd_perfil	= cd_perfil_w
			and	b.nm_usuario	= nm_usuario_p
			and	ie_atualizacao_w	in ('S', 'C');

		end if;

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') then
			begin

			select	coalesce(max('S'),'N')
			into STRICT	ie_atualizacao_w
			from	usuario_setor a
			where	a.nm_usuario		= nm_usuario_p
			and	a.cd_setor_atendimento	= cd_setor_atendimento_w
			and	ie_atualizacao_w	in ('S','C');

			end;
		end if;
		ie_atualizacao_w	:= ie_atualizacao_w;
		end;
	end loop;
	close C01;
end if;

return ie_atualizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ple_obter_acesso_objetivo ( nr_seq_edicao_p bigint, nr_seq_perspectiva_p bigint, nr_seq_tema_p bigint, nr_seq_objetivo_p bigint, nm_usuario_p text) FROM PUBLIC;

