CREATE OR REPLACE FUNCTION ple_obter_aplicacao_meta ( nr_seq_meta_pe_p bigint, nr_seq_indicador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15) := '';


BEGIN

if (nr_seq_meta_pe_p IS NOT NULL AND nr_seq_meta_pe_p::text <> '') then
	select	c.ie_aplicacao
	into STRICT	ds_retorno_w
	from	ple_edicao c,
		ple_objetivo b,
		ple_meta a
	where	a.nr_seq_objetivo	= b.nr_sequencia
	and	b.nr_seq_edicao	= c.nr_sequencia
	and	a.nr_sequencia	= nr_seq_meta_pe_p;
elsif (nr_seq_indicador_p IS NOT NULL AND nr_seq_indicador_p::text <> '') then
	select	c.ie_aplicacao
	into STRICT	ds_retorno_w
	from	ple_edicao c,
		ple_objetivo b,
		indicador_gestao a
	where	a.nr_seq_objetivo_bsc	= b.nr_sequencia
	and	b.nr_seq_edicao		= c.nr_sequencia
	and	a.nr_sequencia		= nr_seq_indicador_p;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
