SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ple_obter_dados_objetivo ( nr_seq_objetivo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);
nr_seq_classif_w			bigint;
nr_seq_tema_w			bigint;
ds_objetivo_w			varchar(4000);


BEGIN

if (nr_seq_objetivo_p IS NOT NULL AND nr_seq_objetivo_p::text <> '') then

	select	max(a.ds_objetivo),
		max(a.nr_seq_classif),
		max(a.nr_seq_tema)
	into STRICT	ds_objetivo_w,
		nr_seq_classif_w,
		nr_seq_tema_w
	from	ple_objetivo a
	where	a.nr_sequencia	= nr_seq_objetivo_p;

	if (ie_opcao_p = 'P') then
		select	coalesce(max(ds_classificacao),'')
		into STRICT	ds_retorno_w
		from	ple_classificacao
		where	nr_sequencia	= nr_seq_classif_w;
	elsif (ie_opcao_p = 'T') then
		select	coalesce(max(ds_titulo),'')
		into STRICT	ds_retorno_w
		from	bsc_tema_estrategico
		where	nr_sequencia	= nr_seq_tema_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_objetivo_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ple_obter_dados_objetivo ( nr_seq_objetivo_p bigint, ie_opcao_p text) FROM PUBLIC;

