CREATE OR REPLACE FUNCTION ple_obter_desc_classif ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w			varchar(80);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	coalesce(max(ds_classificacao),'')
	into STRICT	ds_classificacao_w
	from	ple_classificacao
	where	nr_sequencia	= nr_sequencia_p;

end if;

return substr(ds_classificacao_w,1,80);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
