SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ple_obter_status_meta ( nr_seq_meta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_meta_w		varchar(255);
qt_ordem_aberta_w		integer;
qt_ordem_processo_w		integer;
qt_ordem_encerrada_w	integer;
dt_limite_w				timestamp;


BEGIN

select	ple_count_status_ordem(nr_seq_meta_p, '1'),
		ple_count_status_ordem(nr_seq_meta_p, '2'),
		ple_count_status_ordem(nr_seq_meta_p, '3')
into STRICT	qt_ordem_aberta_w,
		qt_ordem_processo_w,
		qt_ordem_encerrada_w
;


select	dt_limite
into STRICT	dt_limite_w
from	ple_meta
where	nr_sequencia	= nr_seq_meta_p;


if (qt_ordem_aberta_w > 0) and (qt_ordem_processo_w = 0) and (qt_ordem_encerrada_w = 0) then

	ds_status_meta_w	:= obter_desc_expressao(283054);--'Aberta';
end if;

if (qt_ordem_processo_w > 0) and (qt_ordem_encerrada_w = 0) then

	ds_status_meta_w	:= obter_desc_expressao(296477);--'Processo';
end if;

if (qt_ordem_encerrada_w > 0) and (qt_ordem_aberta_w = 0) then

	ds_status_meta_w	:= obter_desc_expressao(303118);--'Conclu√≠da';
end if;

if (qt_ordem_encerrada_w <= 0) and (qt_ordem_aberta_w <> 0) and (dt_limite_w IS NOT NULL AND dt_limite_w::text <> '') and (dt_limite_w < trunc(clock_timestamp())) then

	ds_status_meta_w	:= obter_desc_expressao(302650);--'Atrasada';
end if;

return	ds_status_meta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
