CREATE OR REPLACE FUNCTION ple_obter_usuario_executor ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);
ds_usuario_w	varchar(100);

c01 CURSOR FOR
SELECT	coalesce(b.ds_usuario, a.nm_usuario_exec) nm_exec
from	usuario b,
	man_ordem_servico_exec a
where	a.nm_usuario_exec = b.nm_usuario
and	a.nr_seq_ordem = nr_sequencia_p;


BEGIN

open C01;
loop
fetch C01 into
	ds_usuario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w := substr(ds_retorno_w || ds_usuario_w || ', ',1,4000);
end loop;
close C01;

return substr(ds_retorno_w,1,(length(ds_retorno_w) - 2));

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
