SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_alterar_texto_mensagem ( ds_texto_p text, ds_texto_ini_p text, ds_texto_fim_p text, ds_frase_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_mensagem_w		varchar(4000);
ds_frase_w		varchar(255);
nr_parametro1_w		bigint;
nr_paramentro2_w	bigint;

-- IE_OPCAO_P
-- UP - frase UPPER
BEGIN
/* Obter o primeiro parâmetro para o substr */

nr_parametro1_w := position(ds_texto_ini_p in ds_texto_p);

/* Obter o segundo parâmetro para o substr */

nr_paramentro2_w := position(ds_texto_fim_p in ds_texto_p);

/* Frase para substituição */

ds_frase_w := ds_frase_p;

/* Parâmetros IP_OPCAO_P */

if (upper(ie_opcao_p) = 'UP') then
	ds_frase_w := upper(ds_frase_p);
end if;

/* Trocar frase da mensagem */

select	substr(substr(ds_texto_p,1,nr_parametro1_w - 1) || ds_frase_w || substr(ds_texto_p,nr_paramentro2_w - 1,4000),1,4000)
into STRICT	ds_mensagem_w
;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
