CREATE OR REPLACE FUNCTION pls_analise_obter_status_pag ( ie_status_item_p text, qt_apresentada_p bigint, qt_liberada_p bigint, vl_apresentado_p bigint, vl_calculado_p bigint, vl_liberado_p bigint, vl_glosa_p bigint, ie_glosa_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_pagamento_w		varchar(3)	:= null;


BEGIN

if (coalesce(vl_apresentado_p,0) = 0) and (coalesce(vl_calculado_p,0) = 0) and (coalesce(qt_liberada_p,0) <> coalesce(qt_apresentada_p,0)) then
	if (coalesce(ie_glosa_p,'N') = 'N') then
		ie_pagamento_w	:= 'I'; -- Indefinido
	else
		ie_pagamento_w	:= 'G'; -- Glosado
	end if;
elsif (coalesce(vl_liberado_p,0) = 0) and (coalesce(ie_glosa_p,'N') != 'S') and --Quando é glosado, esse campo é informado "S"
	(coalesce(vl_glosa_p,0) = 0) or (ie_status_item_p in ('U','A','P','M')) then
	ie_pagamento_w	:= 'I'; -- Indefinido
elsif (coalesce(vl_liberado_p,0) > 0) and (coalesce(vl_glosa_p,0) = 0) then
	ie_pagamento_w	:= 'L'; --Liberado
elsif (coalesce(vl_liberado_p,0) > 0) and (coalesce(vl_glosa_p,0) > 0) then
	ie_pagamento_w	:= 'P'; --Parcial
elsif	((coalesce(vl_glosa_p,0) > 0) or (coalesce(ie_glosa_p,'N') = 'S')) and --Quando é glosado, esse campo é informado "S"
	(coalesce(vl_liberado_p,0) = 0) and (coalesce(qt_liberada_p,0) = 0)then
	ie_pagamento_w	:= 'G'; --Glosado
end if;

return	ie_pagamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
