SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_apropriar_juros_multa ( nr_titulo_p titulo_receber.nr_titulo%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_apropriar_juros_multa_w	varchar(1);
ie_pf_pj_w			varchar(1);
ie_origem_titulo_w		titulo_receber.ie_origem_titulo%type;
ie_juros_multa_mens_w		pls_parametros_cr.ie_juros_multa_mens%type;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	ie_origem_titulo,
		CASE WHEN coalesce(cd_cgc::text, '') = '' THEN  'PF'  ELSE 'PJ' END
	into STRICT	ie_origem_titulo_w,
		ie_pf_pj_w
	from	titulo_receber
	where	nr_titulo = nr_titulo_p;

	if (ie_origem_titulo_w = '3') then	-- Titulo de mensalidade
		select	coalesce(max(ie_juros_multa_mens), 'N')
		into STRICT	ie_juros_multa_mens_w
		from	pls_parametros_cr
		where	cd_estabelecimento = cd_estabelecimento_p;

		if (ie_juros_multa_mens_w = 'PF') then
			if (ie_pf_pj_w = 'PF') then
				ie_apropriar_juros_multa_w	:= 'S';
			else
				ie_apropriar_juros_multa_w	:= 'N';
			end if;
		else
			ie_apropriar_juros_multa_w	:= ie_juros_multa_mens_w;
		end if;
	else	-- Outros titulos
		ie_apropriar_juros_multa_w	:= 'N';
	end if;
else
	ie_apropriar_juros_multa_w	:= 'N';
end if;

return	ie_apropriar_juros_multa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
