SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_benef_obter_suspenso ( nr_seq_contrato_p bigint, nr_seq_pagador_p bigint, ie_tipo_p bigint) RETURNS bigint AS $body$
DECLARE


/*	ie_tipo_p
	1 - Quantidade
	2 - Valor rescindido
*/
qt_conta_w			bigint	:= 0;
vl_suspensao_w			double precision	:= 0;
vl_retorno_w			double precision	:= 0;


BEGIN

if (ie_tipo_p	= 1 ) then
	if (nr_seq_contrato_p	> 0) then
		select	count(*)
		into STRICT	qt_conta_w
		from	pls_segurado
		where	nr_seq_contrato = nr_seq_contrato_p
		and	ie_situacao_atend = 'S';
	elsif (nr_seq_pagador_p	> 0 ) then
		select	count(*)
		into STRICT	qt_conta_w
		from	pls_segurado
		where	nr_seq_pagador = nr_seq_pagador_p
		and	ie_situacao_atend = 'S';
	end if;
	vl_retorno_w	:= qt_conta_w;
elsif (ie_tipo_p	= 2) then
	if (nr_seq_contrato_p	> 0) then
		select	sum(b.vl_preco_atual)
		into STRICT	vl_suspensao_w
		from	pls_segurado_preco	b,
			pls_segurado		a
		where	a.nr_sequencia		= b.nr_seq_segurado
		and	a.nr_seq_contrato	= nr_seq_contrato_p
		and	a.ie_situacao_atend = 'S'
		and	b.nr_sequencia	=	(SELECT	max(x.nr_sequencia)
						from	pls_segurado_preco x
						where	x.nr_seq_segurado = a.nr_sequencia);
	elsif (nr_seq_pagador_p	> 0) then
		select	sum(b.vl_preco_atual)
		into STRICT	vl_suspensao_w
		from	pls_segurado_preco	b,
			pls_segurado		a
		where	a.nr_sequencia		= b.nr_seq_segurado
		and	a.nr_seq_pagador	= nr_seq_pagador_p
		and	a.ie_situacao_atend = 'S'
		and	b.nr_sequencia	=	(SELECT	max(x.nr_sequencia)
						from	pls_segurado_preco x
						where	x.nr_seq_segurado = a.nr_sequencia);
	end if;
	vl_retorno_w	:= vl_suspensao_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
