CREATE OR REPLACE FUNCTION pls_buscar_cd_exec_imp (cd_medico_executor_imp_p pls_conta.cd_medico_executor_imp%type, nr_crm_exec_imp_p pls_conta.nr_crm_exec_imp%type, uf_crm_exec_imp_p pls_conta.uf_crm_exec_imp%type, sg_conselho_exec_imp_p pls_conta.sg_conselho_exec_imp%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Buscar o cd_medico_executor_imp, se o mesmo já estiver informado irá retorna-lo
ou se houver informado somente o número do CRM irá buscar o c
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Verificar a performance do select realizado.
Alterações:
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_medico_executor_imp_w	pls_conta.cd_medico_executor_imp%type;


BEGIN
/*cd_medico_executor_imp_p estiver informado retorna ele mesmo*/

if (cd_medico_executor_imp_p IS NOT NULL AND cd_medico_executor_imp_p::text <> '')	then
	cd_medico_executor_imp_w	:= cd_medico_executor_imp_p;
/*Busca o código do médico executor, pelo  que foi informado no XML*/

elsif (nr_crm_exec_imp_p IS NOT NULL AND nr_crm_exec_imp_p::text <> '')	then
	select	max(a.cd_pessoa_fisica)
	into STRICT	cd_medico_executor_imp_w
	from	medico 			a,
		pessoa_fisica 		b,
		conselho_profissional	c
	where	a.uf_crm		= uf_crm_exec_imp_p
	and	a.nr_crm		= nr_crm_exec_imp_p
	and	b.cd_pessoa_fisica 	= a.cd_pessoa_fisica
	and	c.nr_sequencia		= b.nr_seq_conselho
	and	c.sg_conselho		= sg_conselho_exec_imp_p;
else
	/*Se não houver código nem nr_crm retornará nulo*/

	cd_medico_executor_imp_w	:= null;
end if;


return	cd_medico_executor_imp_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
