CREATE OR REPLACE FUNCTION pls_buscar_cd_prof_imp (cd_medico_prof_imp_p pls_conta.cd_medico_executor_imp%type, nr_crm_prof_imp_p medico.nr_crm%type, uf_crm_prof_imp_p medico.uf_crm%type, sg_conselho_prof_imp_p conselho_profissional.sg_conselho%type, cd_versao_tiss_p pls_protocolo_conta.cd_versao_tiss%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Buscar o cd_medico_executor_imp, se o mesmo já estiver informado irá retorna-lo
ou se houver informado somente o número do CRM irá buscar o c
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Verificar a performance do select realizado.
Alterações:
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_medico_w		pls_conta.cd_medico_executor_imp%type;
uf_crm_solic_imp_w	medico.uf_crm%type;
sg_conselho_w		conselho_profissional.sg_conselho%type;


BEGIN
/*cd_medico_executor_imp_p estiver informado retorna ele mesmo*/

if (cd_medico_prof_imp_p IS NOT NULL AND cd_medico_prof_imp_p::text <> '')	then
	cd_medico_w	:= cd_medico_prof_imp_p;
/*Busca o código do médico executor, pelo  que foi informado no XML*/

elsif (nr_crm_prof_imp_p IS NOT NULL AND nr_crm_prof_imp_p::text <> '')	then

	--OS 874735 - Quando buscava o médico pela versão nova, não encontrava devido as alterações dos campos das novas versões
	if (cd_versao_tiss_p >= '3.01.00') then
		uf_crm_solic_imp_w	:= pls_obter_sg_uf_tiss(uf_crm_prof_imp_p);
		begin
		select 	max(sg_conselho)
		into STRICT 	sg_conselho_w
		from 	tiss_conselho_profissional
		where 	cd_conselho = sg_conselho_prof_imp_p;
		exception
		when others then
			sg_conselho_w	:= '';
		end;
	else
		uf_crm_solic_imp_w	:= uf_crm_prof_imp_p;
		sg_conselho_w		:= sg_conselho_prof_imp_p;
	end if;

	select	max(a.cd_pessoa_fisica)
	into STRICT	cd_medico_w
	from	medico 			a,
		pessoa_fisica 		b,
		conselho_profissional	c
	where	a.uf_crm		= uf_crm_solic_imp_w
	and	a.nr_crm		= nr_crm_prof_imp_p
	and	b.cd_pessoa_fisica 	= a.cd_pessoa_fisica
	and	c.nr_sequencia		= b.nr_seq_conselho
	and	c.sg_conselho		= sg_conselho_w;
else
	/*Se não houver código nem nr_crm retornará nulo*/

	cd_medico_w	:= null;
end if;


return	cd_medico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_buscar_cd_prof_imp (cd_medico_prof_imp_p pls_conta.cd_medico_executor_imp%type, nr_crm_prof_imp_p medico.nr_crm%type, uf_crm_prof_imp_p medico.uf_crm%type, sg_conselho_prof_imp_p conselho_profissional.sg_conselho%type, cd_versao_tiss_p pls_protocolo_conta.cd_versao_tiss%type) FROM PUBLIC;

