CREATE OR REPLACE FUNCTION pls_calcular_result ( vl_receita_p bigint, vl_despesa_p bigint) RETURNS bigint AS $body$
DECLARE


vl_resultado_w		double precision := 0;


BEGIN

if (vl_receita_p > vl_despesa_p) then
	vl_resultado_w	:= (1 -(dividir(vl_despesa_p,vl_receita_p))) * 100;
elsif (vl_despesa_p > vl_receita_p) then
	vl_resultado_w	:= ((1 -(dividir(vl_receita_p,vl_despesa_p))) * 100) * -1;
end if;

return	vl_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
