SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_calcular_tempo_xml_fila (nr_seq_xml_arquivo_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_retorno_p : descreve o retorno esperado

'TOT' - O tempo total que o arquivo levou para ser finalizado, desde a sua postagem no webservice
'ESP' - O tempo que o arquivo ficou em estado de espera na fila do web service
'IMPV' - O tempo que o XML levou para se lido e inserido nos campos IMP
'VAL' - O tempo que o sistema levou para validar as informações no banco PLS_GERAR_CONTA / PLS_GERAR_RESUMO_IMPORTACAO
'IMP' - O tempo que o arquivo levou  entre o inicio e o fim da importacao
*/
dt_inicio_importacao_w		timestamp;
dt_inicio_pendente_w		timestamp;
dt_finalizacao_arquivo_w	timestamp;
dt_final_w			timestamp;
dt_inicio_validacao_w		timestamp;
ds_retorno_w			varchar(50);
ie_status_w			varchar(3);


BEGIN

select	a.dt_inicio_importacao,
	a.dt_inicio_pendente,
	a.dt_finalizacao_arquivo,
	a.dt_inicio_validacao,
	a.ie_status
into STRICT	dt_inicio_importacao_w,
	dt_inicio_pendente_w,
	dt_finalizacao_arquivo_w,
	dt_inicio_validacao_w,
	ie_status_w
from	pls_xml_arquivo a
where	nr_sequencia = nr_seq_xml_arquivo_p;

if (ie_retorno_p = 'TOT') then
	if (ie_status_w = 'FIN') then
		dt_final_w	:= (dt_finalizacao_arquivo_w - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'VAL') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;

elsif (ie_retorno_p = 'ESP') then
	if (ie_status_w = 'FIN') then
		dt_final_w	:= (dt_inicio_importacao_w - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'VAL') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (dt_inicio_importacao_w - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;
elsif (ie_retorno_p = 'IMP') then
	if (ie_status_w = 'FIN') then
		dt_final_w	:= (dt_finalizacao_arquivo_w - dt_inicio_importacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'VAL') then
		dt_final_w	:= to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_importacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;
elsif (ie_retorno_p = 'IMPV') then
	if (dt_inicio_validacao_w IS NOT NULL AND dt_inicio_validacao_w::text <> '') then
		dt_final_w	:= (dt_inicio_validacao_w - dt_inicio_importacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'VAL') then
		dt_final_w	:= to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_importacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;
elsif (ie_retorno_p = 'VAL') then
	if (ie_status_w = 'FIN') then
		dt_final_w	:= (dt_finalizacao_arquivo_w - dt_inicio_validacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (coalesce(dt_inicio_validacao_w::text, '') = '') then
		dt_final_w	:= to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_validacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
