SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_cal_tempo_xml_fila_confir (nr_seq_xml_lote_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_retorno_p : descreve o retorno esperado

'TOT' - O tempo total que o lote levou para ser finalizado, desde a sua confirmação no portal
'ESP' - O tempo que o lote ficou em estado de espera na fila do web service
'IMP' - O tempo que o lote levou  entre o inicio e o fim da confirmação
*/
dt_inicio_confirmacao_w		pls_xml_lote.dt_inicio_confirmacao%type;
dt_inicio_pendente_w		timestamp;
dt_finalizacao_confirmacao_w	pls_xml_lote.dt_finalizacao_confirmacao%type;
dt_final_w			timestamp;
ds_retorno_w			varchar(50);
ie_status_w			varchar(3);


BEGIN

select	a.dt_inicio_confirmacao,
	a.dt_inicio_pendente,
	a.dt_finalizacao_confirmacao,
	a.ie_status
into STRICT	dt_inicio_confirmacao_w,
	dt_inicio_pendente_w,
	dt_finalizacao_confirmacao_w,
	ie_status_w
from	pls_xml_lote a
where	nr_sequencia = nr_seq_xml_lote_p;

if (ie_retorno_p = 'TOT') then
	if (ie_status_w = 'FIN') then
		dt_final_w	:= (dt_finalizacao_confirmacao_w - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'VAL') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;

elsif (ie_retorno_p = 'ESP') then
	if (ie_status_w = 'FIN') then
		dt_final_w	:= (dt_inicio_confirmacao_w - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'VAL') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (dt_inicio_confirmacao_w - dt_inicio_pendente_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;
elsif (ie_retorno_p = 'IMP') then
	if (ie_status_w = 'FIN') then
		dt_final_w	:= (dt_finalizacao_confirmacao_w - dt_inicio_confirmacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'VAL') then
		dt_final_w	:= to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');

	elsif (ie_status_w = 'IMP') then
		dt_final_w	:= (clock_timestamp() - dt_inicio_confirmacao_w) + to_date('00:00:00', 'HH24:MI:SS');
		ds_retorno_w	:= to_char(dt_final_w,'HH24:MI:SS');
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
