CREATE OR REPLACE FUNCTION pls_carteira_pag_se_seg ( nr_seq_pagador_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_carteira_w		varchar(30);
nr_seq_contrato_w	bigint;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(b.cd_usuario_plano)
	into STRICT	cd_carteira_w
	from	pls_segurado_carteira	b,
		pls_segurado		a
	where	b.nr_seq_segurado	= a.nr_sequencia
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	a.nr_seq_pagador	= nr_seq_pagador_p;

	if (coalesce(cd_carteira_w::text, '') = '') then
		select	max(a.nr_seq_contrato)
		into STRICT	nr_seq_contrato_w
		from	pls_contrato_pagador a
		where	a.nr_sequencia	= nr_seq_pagador_p;

		select	max(b.cd_usuario_plano)
		into STRICT	cd_carteira_w
		from	pls_segurado_carteira	b,
			pls_segurado		a
		where	b.nr_seq_segurado	= a.nr_sequencia
		and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	a.nr_seq_contrato	= nr_seq_contrato_w
		and	a.nr_seq_pagador	<> nr_seq_pagador_p;
	end if;
end if;

if (coalesce(cd_carteira_w::text, '') = '') then
	select	max(b.cd_usuario_plano)
	into STRICT	cd_carteira_w
	from	pls_segurado_carteira	b,
		pls_segurado		a
	where	b.nr_seq_segurado	= a.nr_sequencia
	and	coalesce(a.nr_seq_titular::text, '') = ''
	and	a.nr_seq_pagador	= nr_seq_pagador_p;
end if;

return	cd_carteira_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
