CREATE OR REPLACE FUNCTION pls_consiste_conta_carteira ( nr_seq_segurado_p bigint, cd_usuario_plano_p text, dt_emissao_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
dt_validade_w	timestamp;


BEGIN
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') and (cd_usuario_plano_p IS NOT NULL AND cd_usuario_plano_p::text <> '') and (dt_emissao_p IS NOT NULL AND dt_emissao_p::text <> '') then
	select	coalesce(dt_validade_carteira,coalesce(dt_emissao_p,clock_timestamp())) dt_validade
	into STRICT	dt_validade_w
	from	pls_segurado_carteira
	where	nr_seq_segurado		= nr_seq_segurado_p
	and	cd_usuario_plano_p	= cd_usuario_plano_p;

	if (trunc(dt_validade_w) < trunc(dt_emissao_p)) then
		ie_retorno_w	:= 'N';
	elsif (trunc(dt_validade_w) >= trunc(dt_emissao_p)) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
