CREATE OR REPLACE FUNCTION pls_consiste_contrato_grupo ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_cpf_p pessoa_fisica.nr_cpf%type, dt_nascimento_p pessoa_fisica.dt_nascimento%type) RETURNS varchar AS $body$
DECLARE


nr_seq_grupo_w 		pls_contrato_grupo.nr_seq_grupo%type;
ie_consistir_w		varchar(1) := 'N';
qt_pessoas_w		bigint	:= 0;


BEGIN
select 	max(a.nr_seq_grupo)
into STRICT	nr_seq_grupo_w
from 	pls_contrato_grupo a
where 	nr_seq_contrato = nr_seq_contrato_p;

if (coalesce(nr_seq_grupo_w,0) > 0) then
	select	count(*)
	into STRICT	qt_pessoas_w
	from   	pls_contrato_grupo a,
		pls_segurado b,
		pessoa_fisica c
	where  	a.nr_seq_contrato 	= b.nr_seq_contrato
	and    	b.cd_pessoa_fisica 	= c.cd_pessoa_fisica
	and    	a.nr_seq_grupo 		= nr_seq_grupo_w
	and 	c.nr_cpf		= nr_cpf_p
	and 	c.dt_nascimento		= dt_nascimento_p
	and    ((coalesce(b.dt_rescisao::text, '') = '')
	or     ((clock_timestamp() - b.dt_rescisao) <= 30));
end if;

if (qt_pessoas_w > 0) then
	ie_consistir_w := 'S';
end if;

return ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
