CREATE OR REPLACE FUNCTION pls_consiste_vigencia_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_procedimento_p timestamp) RETURNS varchar AS $body$
DECLARE


/* A function irá retornar "S" caso não exista regra de vigência para o procedimento ou se existir, o procedimento lançado estiver dentro da vigência */

ie_proc_valido_w		varchar(1)	:= 'S';
qt_registro_w			bigint	:= 0;


BEGIN
/* Obter se existe regra de vigência para o procedimento */

select	count(*)
into STRICT	qt_registro_w
from	pls_procedimento_vigencia
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

if (qt_registro_w	> 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_proc_valido_w
	from	pls_procedimento_vigencia
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	trunc(dt_procedimento_p) between trunc(dt_inicio_vigencia) and trunc(coalesce(dt_fim_vigencia,dt_procedimento_p));
end if;

return	ie_proc_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
