CREATE OR REPLACE FUNCTION pls_consistir_gerar_tit_rec (nr_seq_ptu_fatura_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_titulo_pagar_w	bigint;
ie_situacao_titulo_w	varchar(2);
qt_baixa_glosa_w	bigint;


BEGIN
/* Tratamento para a geração de título a receber a partir da contestação */

if (nr_seq_ptu_fatura_p IS NOT NULL AND nr_seq_ptu_fatura_p::text <> '') then
	select	max(b.nr_titulo),
		max(b.ie_situacao)
	into STRICT	nr_titulo_pagar_w,
		ie_situacao_titulo_w
	from	titulo_pagar b,
		ptu_fatura a
	where	a.nr_titulo	= b.nr_titulo
	and	a.nr_sequencia	= nr_seq_ptu_fatura_p;

	select	count(*)
	into STRICT	qt_baixa_glosa_w
	from	titulo_pagar_baixa a
	where	a.nr_titulo	= nr_titulo_pagar_w
	and	a.vl_glosa	> 0;

	if (qt_baixa_glosa_w = 0) and (ie_situacao_titulo_w = 'L') then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_consistir_gerar_tit_rec (nr_seq_ptu_fatura_p bigint) FROM PUBLIC;

